  case class Base(name: String) {
    def contains(catName: String): Boolean = catName == name
  }

  case class Intermediate(name: String, categories: List[Base]) {
    def contains(catName: String): Boolean = categories.map(_.name) contains catName

    def categoriesList: List[String] = categories.map(_.name)
  }

  case class Superior(name: String, categories: List[Intermediate]) {
    def contains(catName: String): Boolean = categories.flatMap(_.categoriesList) contains catName
  }

